svg.script:
var types = [ "chapter" ,"verse" ]

$( document ).ready(function() {
for (i = 0; i < types.length; i++) {
    var type=types[i]
        $( "#" + type + "-description" ).hide();
    }
});

for (i = 0; i < types.length; i++) {
    var type=types[i]

    $( "#"+ type +"-node-sample" ).mouseenter(function() {
        $( "#" + type + "-description" ).fadeIn(500);
    });

    $( "#"+ type +"-node-sample" ).mouseleave(function() {
        $( "#" + type + "-description" ).fadeOut(500);
    });
}
